set -euo pipefail
docker run --rm -d -p 10080:80 -p 10443:443 --name=web --net-alias=public.tvbit.co --net-alias=master.tvbit.co --mount type=bind,source=$(pwd)/dev_config/ssl,target=/etc/nginx/ssl --mount type=bind,source=$(pwd)/dev_src/supertvbit/public/public,target=/root/web/public --mount type=bind,source=$(pwd)/dev_src/supertvbit/public/panel-build,target=/root/web/panel-build --mount type=bind,source=$(pwd)/dev_src/supertvbit/master/php,target=/root/web/master --network=tvbitnet web